mapper.select:
SELECT SEQ,ID,TITLE,READCOUNT,REFER, STEP,DEPTH,REGDATE, DELFLAG FROM ANSWERBOARD a
SEQ = #{seq}
ORDER BY REFER DESC, STEP ASC

SELECT SEQ,ID,TITLE,CONTENT,READCOUNT,REFER, STEP,DEPTH,REGDATE,DELFLAG
FROM ANSWERBOARD a
WHERE SEQ = #{seq}

mapper.update:
UPDATE ANSWERBOARD SET DELFLAG = 'Y'
WHERE SEQ IN

	#{seq}
